    get.xwOriginGroup = function(player){
        if(!game.xwGroupChanged()){
            return player.group;
        }
        var name;
        if(player.name){
            name = player.name;
        }else if(player.name1){
            name = player.name1;
        }
        if(!name){
            return player.group;
        }
        var info = lib.character[name];
        if(!info){
            return player.group;
        }
        if(info[4] && info[4].length > 0){
            if(info[4][0].indexOf('xwjhMp')<0){
                return player.group;
            }
        }
        if(name.indexOf('xwjh_') != 0){
            return player.group;
        }
        if(player.group == 'shu'){
            return 'xwjh_zheng';
        }
        if(player.group == 'wei'){
            return 'xwjh_xie';
        }
        if(player.group == 'wu'){
            return 'xwjh_chao';
        }
        if(player.group == 'qun'){
            return 'xwjh_ye';
        }
        return player.group;
    };

