                        if(source.maxHp - source.hp == 1 && source.hasSkill('xwjh_diwei')){
                            return false;
                        }
                        var counth = source.countCards('h');
                        if(source.getCardUsable('sha') && counth >= source.hp){
                            return false;
                        }
                        if(counth > source.maxHp){
                            return false;
                        }
